// Function to double each element in an array
function doubleArray(arr) {
  //Loop over each element in the array
  for (var i = 0; i < arr.length; i++) {
    //Double the element
    arr[i] = arr[i] * 2;
  }
  //Return the modified array
  return arr;
}

