const evenNumberArray = (array) => {
    let newArray = [];
    for (let i = 0; i < array.length; i++) {
        const num = array[i];
        if (num % 2 === 0) {
            newArray.push(num);
        }
    }
    return newArray;
}

const array = [1, 2, 3, 4, 5, 6, 7, 8];

console.log(evenNumberArray(array)); // Output: [2, 4, 6, 8]

