def count_words(str): 
  counts = dict() 
  words = str.split() 

  for word in words: 
    if word in counts: 
      counts[word] += 1
    else: 
      counts[word] = 1

  return counts

def find_longest_word(str): 
  words = str.split() 
  longest_word = ""
  longest_length = 0
  for word in words: 
    word_length = len(word) 
    if word_length > longest_length:
      longest_length = word_length
      longest_word = word 

  return longest_word
