numbers = [3, 5, 7, 10, 10, 5, 8, 12]

# Create a dictionary to store the frequency of each number
frequency = {}

# Count the frequency of each number
for num in numbers:
	if num in frequency:
		frequency[num] += 1
	else:
		frequency[num] = 1

# Find the mode(s) with the highest frequency
mode = []
max_freq = max(frequency.values())
for key, value in frequency.items():
	if value == max_freq:
		mode.append(key)

# Print the mode(s)
if len(mode) == 1:
	print("The mode is:", mode[0])
else:
	print("The modes are:", mode)
