"""
Remove all vowels from a string
"""

def remove_vowels(string):
    vowels = set(['a', 'e', 'i', 'o', 'u'])
    result = ''
    for char in string:
        if char not in vowels:
            result += char
    return result

if __name__ == '__main__':
    print(remove_vowels('hello world'))
