#include <iostream>
#include <fstream>
#include <string>

using namespace std;

int main() {
    // Declare variables
    string line;
    int count = 0;

    // Open text file
    ifstream myfile("file.txt");

    // Read the file line by line
    while (getline(myfile, line)) {
        // Split the line by any whitespaces
        istringstream iss(line);
        string word;

        // Read each word and increment the word count
        while (iss >> word) {
            count++;
        }
    }

    // Output the word count
    cout << "Number of words: " << count << endl;

    return 0;
}

class Solution:
    def checkValidString(self, s: str) -> bool:
        open_stack = []
        star_stack = []
        for i, char in enumerate(s):

            # Add open brackets to open_stack and star to star_stack
            if char == "(":
                open_stack.append(i)
            elif char == "*":
                star_stack.append(i)

            # Check the current opening brackets or star, pop them in case they match the closing bracket
            # If no matching bracket is found, then the expression is not valid.
            elif char == ")":
                if open_stack:
                    open_stack.pop()
                elif star_stack:
                    star_stack.pop()
                else:
                    return False

        # Check the length of the remaining stacks
        while open_stack and star_stack:
            o = open_stack.pop()
            s = star_stack.pop()

            if o > s:
                return False

        return len(open_stack) == 0
